#!/usr/bin/env node

'use strict';
let Kafka = require('node-rdkafka');
let jobid =  `${process.env.JOB_EVENT_TITLE}${process.env.JOB_ID}`.replace(' ','');
const topicToProduce = process.env.TOPIC_TO_PRODUCE ;
const topicToConsume = process.env.TOPIC_TO_CONSUME ;
let requestDelay = process.env.REQUEST_DELAY || 10000;

//validate();

console.log('Creating producer')
var producer = new Kafka.Producer({
  'metadata.broker.list': process.env.BROKER_LIST,
  'client.id': jobid ,
  'dr_cb': true
});
//message body
let trigger = {
  timeout : Number(process.env.JOB_TIMEOUT),
  jobid :  jobid || 'jobid' ,
  processingTime : process.env.PROCESSING_TIME  ,
  error : process.env.IN_ERROR || false,
  operation: 'Start'
};
// message key
let key = {jobid: jobid};

console.log('Producer connection');


// start consuming response
var consumer = new Kafka.KafkaConsumer({
  //'debug': 'all',
  'metadata.broker.list': process.env.BROKER_LIST,
  'group.id': jobid,
  'enable.auto.commit': true
} , {
    'auto.offset.reset' : 'earliest'
});
//logging debug messages, if debug is enabled
consumer.on('event.log', function(log) {
  console.log(log);
});
//logging all errors
consumer.on('event.error',onConsumerError);
// when consumer is ready , subscribe to topics
consumer.on('ready', function(arg) {
  console.log('consumer ready.' + JSON.stringify(arg));
  consumer.subscribe([topicToConsume]);
  //start consuming messages
  consumer.consume();
  // Connect to the broker manually
  producer.connect();
  // Wait for the ready event before proceeding
  producer.on('ready', function() {
    try {
      console.log('Producer is ready');
      // send message to job trigger
      producer.produce(topicToProduce,null,new Buffer(JSON.stringify(trigger)),new Buffer(JSON.stringify(key)),Date.now(),);
    } catch (err) {
      console.error('A problem occurred when sending our message');
      console.error(err);
      process.exit(1)
    }
  });


});
consumer.on('data', function(msg) {
  // Output the actual message contents
  console.log(JSON.stringify(msg));
  let record = JSON.parse(msg.value.toString());
  console.log('A message received');
  if(record.jobid === jobid){
    consumer.disconnect();
    onNextMsgReceived(record);
  }
});
consumer.on('disconnected', function(arg) {
  console.log('consumer disconnected. ' + JSON.stringify(arg));
});
//starting the consumer
consumer.connect();

process.on('SIGTERM',()=>{
  console.log('Sending a message to abort job');
  consumer.disconnect();
  trigger.operation = 'Abort';
  // send message to job trigger
  producer.produce(topicToProduce,null,new Buffer(JSON.stringify(trigger)),new Buffer(JSON.stringify(key)),Date.now(),);
})


/**
* Function execute on next message from kafka for a consumer
*/
function onNextMsgReceived(value){
  console.log(`Job response received ${value.jobid} , status : ${value.result} , percentage : ${value.percentage}`);
  console.log(`${value.percentage}%`);
  if(value.result === 'Success' && value.percentage === 100){
    process.exit();
  }else if (value.result === 'Failure'){
    process.exit(1);
  }
}

/**
* Function to validate if environment variable are properly configured
*
*/
function validate() {
  if(!process.env.JOB_ID) {
    console.error('JOBID not defined !');
    process.exit(1);
  }else if(!process.env.JOB_EVENT_TITLE) {
    console.error('JOB_EVENT_TITLE not defined !');
    process.exit(1);
  } else if(!process.env.JOB_TIMEOUT){
    console.error('JOB_TIMEOUT not defined !');
    process.exit(1);
  } else if(!process.env.TOPIC_TO_CONSUME) {
    console.error('TOPIC_TO_CONSUME not defined !');
    process.exit(1);
  }else if(!process.env.TOPIC_TO_PRODUCE) {
    console.error('TOPIC_TO_PRODUCE not defined !');
    process.exit(1);
  }else if(!process.env.BROKER_LIST) {
    console.error('BROKER_LIST not defined !');
    process.exit(1);
  }
}

/**
*   An error occurs during a topic creation , so you need to shutdown a consumer instance
*/
function onConsumerError(err){
  console.log(`Something wrong consuming event ${topicToConsume} : ${err}`);
  process.exit(1);
}

// Any errors we encounter, including connection errors
producer.on('event.error', function(err) {
  console.error('Error from producer');
  console.error(err);
  process.exit(1);
})
